    def fill_table(self,list_items: list,model):

        for row, list_data in enumerate(list_items):
            for col, data in enumerate(list_data):
                self.set_item(row, col, str(data),model)

    def set_item(self, row, col, data,model):
       item = QStandardItem(data)
       model.setItem(row, col, item)

    def not_editable_table(self,model):
        for row in range(model.rowCount()):
            for column in range(model.columnCount()):
                item = model.item(row, column)
                if item is not None:
                    item.setEditable(False)

    def get_row_selection(self, index):
        fila_seleccionada = index.row()
        datos_fila = []
        for columna in range(self.model.columnCount()):
            item = self.model.item(fila_seleccionada, columna)
            datos_fila.append(item.text())
        tupla_fila = tuple(datos_fila)
        self.lbl_msj_suppier_selected.setText(f"Se asignaran los productos seleccionados a el provedor:{tupla_fila[0]}")
        self.row_data= tupla_fila

