    def get_item_change(self, topLeft: QModelIndex,model):
        # Obtener las coordenadas de la celda que ha cambiado
        row = topLeft.row()
        column = topLeft.column()
        self.column_header = model.headerData(column, Qt.Orientation.Horizontal)
        id = model.index(row, 0)
        first_cell_attribute = model.data(id)
        new_value = model.data(topLeft)
        if model == self.model_prod:
            self.prod_cahnge=(first_cell_attribute,self.column_header,new_value)
        if model == self.model_orders:
            if self.column_header =="Fecha Realiza":
                colum_date_delivery=column+1
                index = self.model_orders.index(row, colum_date_delivery)
                self.date_delivery = self.model_orders.data(index)
            elif self.column_header =="Fecha Entrega":
                colum_date_initial=column-1
                index = self.model_orders.index(row, colum_date_initial)
                self.colum_date_initial = self.model_orders.data(index)
            elif self.column_header == "Cantidad":
                column_id_prod=column-1
                index = self.model_orders.index(row, column_id_prod)
                self.column_id_prod = self.model_orders.data(index)
            self.order_change=(first_cell_attribute,self.column_header,new_value)
        if model==self.model_sale:
            id_prod = model.index(row, 3)
            id_prod_sale= model.data(id_prod)
            self.sale_change_list=(first_cell_attribute,id_prod_sale,self.column_header,new_value)
            self.sale_change=list(self.sale_change_list)




