    def unsubscribe_rows_orders(self,id_order,id_prod):
        rows_to_delete = []
        for row in range(self.model_orders.rowCount()):
            id_item = self.model_orders.item(row, 0)
            #other_item = self.model_orders.item(row, 7)
            if id_item is not None and id_item.text() == id_order:
                rows_to_delete.append(row)  # Guardamos el √≠ndice de fila

        for row in reversed(rows_to_delete):
            #id_item = self.model_orders.item(row, 0)  # Assuming ID is in the first column
            other_item = self.model_orders.item(row, 7)  # Assuming other item is in the eighth column
            if id_prod == "":
                self.model_orders.removeRow(row)  # Si el ID de producto est√° vac√≠o, eliminamos la fila
            elif other_item is not None and other_item.text() == id_prod:
                self.model_orders.removeRow(row)  # Si el ID de producto coincide, eliminamos la fila
